const splitInHalf = (arr) => {

    let arrayOfHalves = [];

        for (let i = 0; i < arr.length; i++) {
            const firstHalf = arr[i].slice(0, arr[i].length / 2).split("");
            const secondHalf = arr[i].slice(arr[i].length / 2).split("");
            
            const twoCompartmentsArray = [firstHalf, secondHalf]
            
            arrayOfHalves.push(twoCompartmentsArray);
    }
    return arrayOfHalves;
}

