const groupOfThree = (arr) => {
    const groupSize = 3;
    let arrayOfGroups = [];
for (let i = 0; i < arr.length; i += groupSize) {
    const group = arr.slice(i, i + groupSize);
    arrayOfGroups.push(group);
}
    return arrayOfGroups;
}

